mapper.select:
select *
from attraction_info


		and sido_code = #{sidoCode}
		and gugun_code = #{gugunCode}


		and content_type_id in

			#{item}




		and title LIKE '%${keyword}%'

select count(rating)
from review
where place_id = #{contentId}

select avg(rating)
from review
where place_id = #{contentId}

mapper.update:
update review
set content = #{content}, rating = #{rating}
where review_id = #{reviewId}
